function readout(x::String)
  numbers = readdlm(IOBuffer(x), Int)
  s= reshape(numbers, 64,64)
  for i in 1:length(s)
    if s[i]== 1 && rand(Binomial(1,0.5))==1
      s[i]=-1
    end
  end
  s= s/norm(s)
  a= real(eigvecs(s))
  a1= a[:,1]
 return a1
end


function EV(H)
  a= eigvecs(H)
  a1= a[:,1]
  return a1
end
#definiere up down Spins
u=[1,0]
d=[0,1]

#bidle Funktion für äußeres Produkt
function basis(x,n)
  if n>1
    A=[]
    B= basis(x,n-1)
    for i in x
      for j in B
        push!(A, kron(i,j))
      end
    end
    return A
  else
    return x
  end
end

function buildPsi(x,N,a)
  b=N-a
  B=[]
  state=spzeros(2^a,2^b)
  basisA=basis([u,d],a)
  basisB=basis([u,d],b)

  for i in 1:length(basisA)
    for j in 1:length(basisB)

      push!(B,kron(basisA[i],basisB[j]))
    end
  end

  l=1
  for i in 1:2^a
    for j in 1:2^b
      state[i,j]=dot(x,B[l])
      l+=1
    end
  end
  return state
end


function rdm(X,a)
  ρ::Array{Float64,2} = zeros(2^a,2^a)
  for i in 1:size(X)[1]
    for k in 1:size(X)[1]
      for j in 1:size(X)[2]
        ρ[i,k]+= sum(X[i,j]*X[k,j])
      end
    end
  end
  return ρ
end

function vrs(N)
  w::AbstractArray = eigvals(N)
  V::Float64 = 0
  for i = 1:length(w)
    if 0> w[i] <-1.0e-14
      println(w[i])
      throw("eigenwert ist negativ")
    elseif abs(w[i]) < 1.0e-5
      w[i] = 0.0
    else
      V += (w[i]*log(w[i]))
    end
  end
  return V
end

