function dataT(N)
  df = DataFrame(a1 = statT(N,1), a2 = statT(N,2), a3 =statT(N,3), a4 =statT(N,4), a5=statT(N,5))
  return df
end

#Plot der Verschränkung
function pltZ(N,a)
  b= N-a
  #Lösche die Eintraege aus vorheriger Schleife
  x = statZ(N,a)
  xm = floor(mean(x),4)

  #Plotting Theme
  blankTheme = Theme(
  grid_color=colorant"grey",
  panel_fill=colorant"white",
  major_label_color=colorant"black",
  minor_label_color=colorant"black")
  plot_padding = 3pt
  x[1]=0
  x[end]=2.5
  p = plot(x=x , y=1:length(x), Guide.title("Verschränkung zufälliger Zustände bei $N Plätzen "),
  Guide.XLabel("Aufteilung der Plätze in A= $a, B= $b \n Mittelwert von V = $xm "),Guide.YLabel("Haeufigkeit"),
  blankTheme, Geom.histogram(bincount= floor(1000*log(2))))
   img = PDF("plotZV/Verschraenkung $N _$a.pdf", 8inch, 6inch)
   draw(img, p)
  return p
end

